		/*
		 * Remove the tasklet from the CPU's list of tasklets
		 * and run it.
		 */
		tasklet_unlock_function(tasklet, &flags);
		tasklet_unlock_wait(tasklet);
		local_irq_restore(flags);
	} else {
		/*
		 * This tasklet is being run in bottom half context,
		 * which can potentially reschedule.  So we need to
		 * protect against an interrupt coming in and doing
		 * an __run_task_queue() on our tasklet.  tasklet_lock()
		 * will prevent an interrupt from doing an
