		 * will be scheduled by __run_task_queue() to run later
		 * in tasklet_try_to_wake_up().
		 */
		tasklet_lock(tasklet, &flags);
		if (tasklet->next == NULL)
			tasklet_unlock_function(tasklet, &flags);
		else {
			tasklet_unlock_wait(tasklet);
			local_irq_restore(flags);
